import React, { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import { Send, Upload, ChevronLeft } from "lucide-react";
import BackButton from "../../components/shared/BackButton";
import ReactMarkdown from 'react-markdown'; // Changed from named import to default import

// Your Gemini API key should be in .env file
const GEMINI_API_KEY = process.env.REACT_APP_GEMINI_API_KEY || "AIzaSyDIj_BIwaYHQoNKDkZxdf1IZ3KtMS5lk2Y";

const NewCheckup = () => {
  const { id } = useParams();
  const [chatMessages, setChatMessages] = useState([
    {
      role: "assistant",
      content: "Hello! I'm your AI assistant. How can I help you with this checkup?",
    },
  ]);
  const [inputMessage, setInputMessage] = useState("");
  const [prescriptionNotes, setPrescriptionNotes] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const chatContainerRef = useRef(null);

  // Auto-scroll to bottom of chat
  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [chatMessages]);

  // Function to call Gemini API
  const callGeminiAPI = async (message) => {
    setIsLoading(true);
    
    try {
      const generationConfig = {
        temperature: 1,
        topP: 0.95,
        topK: 40,
        maxOutputTokens: 8192,
      };

      // Start with the system prompt and include chat history
      const chatHistory = chatMessages
        .filter(msg => msg.role !== "system")
        .map(msg => ({
          role: msg.role === "assistant" ? "model" : "user",
          parts: [{ text: msg.content }]
        }));

      // Add the new message
      chatHistory.push({
        role: "user",
        parts: [{ text: message }]
      });

      const data = {
        generationConfig,
        contents: chatHistory,
        systemInstruction: {
          role: "user",
          parts: [{
            text: 'You are a healthcare assistant chatbot. When a doctor or user sends you a prescription (in plain text or structured format), your job is to:\n\nRead and extract the key prescription details such as:\n\nDrug names\n\nDosage\n\nFrequency\n\nDuration\n\nInstructions (e.g., "after food", "before bed")\n\nStart a conversational response to:\n\nConfirm and summarize the prescription\n\nAsk any necessary clarifying questions (e.g., missing dosage or duration)\n\nAlert if there is anything unusual, like high dosages or potential duplicate medications\n\n(Optional) Based on access to patient history (if provided), suggest:\n\nDrug-drug interactions\n\nRisk factors\n\nSafer alternatives\n\nKeep your tone professional, helpful, and non-alarming.\n\nNever make final medical decisions\n\nAlways defer to the prescribing doctor for confirmation\n\nIf asked, generate a clean version of the prescription or save it for upload.\n\nYour goal is to act like a smart assistant that "thinks out loud" about the prescription and helps verify or enhance it with intelligence — but always with the doctor in control.'
          }]
        },
      };

      const response = await fetch(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash-lite:generateContent?key=${GEMINI_API_KEY}`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(data)
        }
      );

      const responseData = await response.json();
      
      if (responseData.candidates && responseData.candidates[0]?.content?.parts[0]?.text) {
        return responseData.candidates[0].content.parts[0].text;
      } else {
        console.error("Unexpected API response:", responseData);
        return "I'm sorry, I couldn't process that request. Please try again.";
      }
    } catch (error) {
      console.error("Error calling Gemini API:", error);
      return "I apologize, but I encountered an error. Please try again later.";
    } finally {
      setIsLoading(false);
    }
  };

  // Function to handle sending the initial prescription
  const handleSharePrescription = async () => {
    if (!prescriptionNotes.trim()) {
      alert("Please enter prescription details first.");
      return;
    }

    setIsLoading(true);
    
    // Prepare the message with prescription notes
    const message = `Prescription:\n\n${prescriptionNotes}`;
    
    // Add user message to chat
    setChatMessages(prev => [
      ...prev,
      { role: "user", content: message }
    ]);

    // Get AI response
    const aiResponse = await callGeminiAPI(message);
    
    // Add AI response to chat
    setChatMessages(prev => [
      ...prev,
      { role: "assistant", content: aiResponse }
    ]);
    
    setIsLoading(false);
  };

  // Function to handle sending a message
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || isLoading) return;

    // Add user message to chat
    setChatMessages(prev => [
      ...prev, 
      { role: "user", content: inputMessage }
    ]);
    
    // Clear input field
    const messageToBeSent = inputMessage;
    setInputMessage("");

    // Get AI response
    const aiResponse = await callGeminiAPI(messageToBeSent);
    
    // Add AI response to chat
    setChatMessages(prev => [
      ...prev,
      { role: "assistant", content: aiResponse }
    ]);
  };

  // Function to handle uploading prescription
  const handleUploadPrescription = () => {
    // Add your upload logic here
    console.log("Uploading prescription:", prescriptionNotes);
    // You could save this to a database or blockchain here
    alert("Prescription uploaded successfully!");
  };

  return (
    <div className="h-[calc(100vh-theme(spacing.16))] flex flex-col overflow-hidden">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 p-4 flex-shrink-0">
        <BackButton />
        <h2 className="text-xl font-semibold text-gray-800 mt-2">
          New Checkup Session
        </h2>
        <p className="text-sm text-gray-500">Patient ID: {id}</p>
      </div>

      {/* Split View Container */}
      <div className="flex-1 flex overflow-hidden"> 
        {/* Left Half - Chat Interface */}
        <div className="w-1/2 border-r border-gray-200 flex flex-col bg-white overflow-hidden">
          <div className="p-4 border-b border-gray-200 flex-shrink-0">
            <h3 className="text-lg font-medium text-gray-900">AI Assistant</h3>
          </div>

          {/* Scrollable Chat Area */}
          <div 
            ref={chatContainerRef}
            className="flex-1 overflow-y-auto p-4 space-y-4" 
          >
            {chatMessages.map((message, index) => (
              <div
                key={index}
                className={`flex ${
                  message.role === "user" ? "justify-end" : "justify-start"
                }`}
              >
                <div
                  className={`max-w-[80%] rounded-lg px-4 py-2 ${
                    message.role === "user"
                      ? "bg-blue-600 text-white"
                      : "bg-gray-100 text-gray-900"
                  }`}
                >
                  {message.role === "assistant" ? (
                    <div className="markdown">
                      {/* Basic formatting for bold text */}
                      {message.content.split(/(\*\*.*?\*\*)/).map((part, i) => {
                        if (part.startsWith('**') && part.endsWith('**')) {
                          return <strong key={i}>{part.slice(2, -2)}</strong>;
                        }
                        return <span key={i}>{part}</span>;
                      })}
                    </div>
                  ) : (
                    message.content
                  )}
                </div>
              </div>
            ))}
            {isLoading && (
              <div className="flex justify-start">
                <div className="max-w-[80%] rounded-lg px-4 py-2 bg-gray-100 text-gray-900">
                  <div className="flex space-x-2">
                    <div className="h-2 w-2 bg-gray-400 rounded-full animate-bounce"></div>
                    <div className="h-2 w-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                    <div className="h-2 w-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.4s'}}></div>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Message Input Form - Fixed at bottom */}
          <form
            onSubmit={handleSendMessage}
            className="p-4 border-t border-gray-200 flex-shrink-0"
          >
            <div className="flex space-x-2">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder="Type your message..."
                className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                disabled={isLoading}
              />
              <button
                type="submit"
                className={`px-4 py-2 bg-blue-600 text-white rounded-lg transition-colors ${
                  isLoading ? 'opacity-50 cursor-not-allowed' : 'hover:bg-blue-700'
                }`}
                disabled={isLoading}
              >
                <Send className="w-5 h-5" />
              </button>
            </div>
          </form>
        </div>

        {/* Right Half - Prescription Editor */}
        <div className="w-1/2 flex flex-col bg-white overflow-hidden">
          <div className="p-4 border-b border-gray-200 flex-shrink-0">
            <h3 className="text-lg font-medium text-gray-900">
              Prescription Notes
            </h3>
          </div>

          {/* Scrollable Prescription Area */}
          <div className="flex-1 p-4 overflow-hidden">
            <textarea
              value={prescriptionNotes}
              onChange={(e) => setPrescriptionNotes(e.target.value)}
              placeholder="Type your prescription notes here..."
              className="w-full h-full p-4 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 overflow-y-auto"
            />
          </div>

          {/* Action Buttons - Fixed at bottom */}
          <div className="p-4 border-t border-gray-200 flex flex-col space-y-2 flex-shrink-0">
            <button
              onClick={handleSharePrescription}
              className="w-full px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors flex items-center justify-center mb-2"
              disabled={isLoading || !prescriptionNotes.trim()}
            >
              <Send className="w-5 h-5 mr-2" />
              Share with AI Assistant
            </button>
            
            <button
              onClick={handleUploadPrescription}
              className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center justify-center"
              disabled={isLoading || !prescriptionNotes.trim()}
            >
              <Upload className="w-5 h-5 mr-2" />
              Upload Prescription
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewCheckup;
